class Solution {
public:

    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>dp(k+1,vector<int>(n,0));

        map<int,int>mp;
        for(int i=0;i<n;i++){
            mp[nums[i]]++;
            for(auto &el:mp){
                dp[0][i]=max(dp[0][i],el.second);
            }
        }

        for(int i=1;i<=k;i++){
            set<pair<int,int>>st;
            map<int,int>mp;
            for(int j=0;j<n;j++){
                int x=nums[j];
                if(st.size()==0){
                    dp[i][j]=1;
                }else{
                    auto it=st.end();
                    --it;
                    auto pr=(*it);
                    dp[i][j]=max(dp[i][j],pr.first+1);
                    dp[i][j]=max(dp[i][j],mp[x]+1);
                }
                mp[x]=dp[i][j];
                st.insert({dp[i-1][j],nums[j]});

            }
        }
        int maxi=0;

        for(int i=0;i<=k;i++){
            for(int j=0;j<n;j++){
                maxi=max(maxi,dp[i][j]);
            }
            // cout<<endl;
        }
        return maxi;
    }
};
