class Solution {
  public:
   int maximumLength(vector<int>& nums, int k) {
      int n = nums.size();
      vector<pair<int,int>> a;
      for(int i=0;i<n;) {
         int j=i;
         while(j<n && nums[j]==nums[i]) j++;
         a.push_back({nums[i],(j-i)});
         i=j;
      }
      n = a.size();
      vector<int> dp(k+2,0);
      map<int,vector<int>> mp;
      for(int i=0;i<n;i++) {
         int x = a[i].first, val = a[i].second;
         vector<int> cur = dp, pt(k+2);
         for(int j=0;j<=k;j++) {
            cur[j+1] = max(cur[j+1],dp[j]+val);
             pt[j+1] = dp[j]+val;
         }
         if(mp.find(x) != mp.end()) {
            int j=0;
                for(auto c: mp[x]) {
                    if(j>0) cur[j] = max(cur[j],c+val),pt[j]=max(pt[j],c+val);
                    j++;
                }

         }
         mp[x]=pt;
         dp = cur;
          //for(auto it: dp) cout<<it<<' ';
          //cout<<"\n";
      }
       //for(auto [x,y]: a) cout<<x<<' '<<y<<'\n'; 
       //for(auto it: dp) cout<<it<<' ';
       //cout<<'\n';
      return *max_element(dp.begin(),dp.end()); 
   }
};
