#define ll long long
#define pb push_back
#define fr(i,a,b) for(int i = a; i < b; i++)
#define mod 1000000007
#define inf (1LL<<60)
#define all(x) (x).begin(), (x).end()
#define vll vector<ll> 
#define pll pair<ll,ll>

class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size(); 
        int utt=1; 
        int w= n*utt - n;
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        vector<int>temp1(k+1,-1); 
        vector<map<int,int>>temp1wMap(k+1); 
        for(int i=0;i<=n-1;i++){ 
            for(int j=0;j<k+1;j++){ 
                for(int p=0;p<=j-1;p++){ 
                    dp[i][j]=max(temp1[p]+1,dp[i][j]); 
                } 
                if(temp1wMap[j].find(nums[i])!=temp1wMap[j].end()){
                     int x=nums[i]; 
                    dp[i][j]=max(temp1wMap[j][x]+1,dp[i][j]); 
                } 
                utt=max(utt,dp[i][j]); 
            } 
            for(int v=0;v<k+1;v++){ 
               temp1wMap[v][nums[i]]=max(temp1wMap[v][nums[i]],dp[i][v]); 
                temp1[v]=max(dp[i][v],temp1[v]); 

            } 
        } 
        return utt; 
    }
};
