class Solution {
    public int maximumLength(int[] nums, int k) {
        int n = nums.length;
        int[][] dp = new int[n][k + 1];
        for (int i = 0; i < n; i++) {
            Arrays.fill(dp[i], 1);
        }
        int ans = 1;
        int[] ans1 = new int[k + 1];
        Arrays.fill(ans1, -1);
        Map<Integer, Integer>[] ans2 = new HashMap[k + 1];
        for (int i = 0; i <= k; i++) {
            ans2[i] = new HashMap<>();
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                for (int l = 0; l < j; l++) {
                    dp[i][j] = Math.max(dp[i][j], 1 + ans1[l]);
                }
                if (ans2[j].containsKey(nums[i])) {
                    dp[i][j] = Math.max(dp[i][j], 1 + ans2[j].get(nums[i]));
                }
                ans = Math.max(ans, dp[i][j]);
            }
            for (int j = 0; j <= k; j++) {
                ans1[j] = Math.max(ans1[j], dp[i][j]);
                ans2[j].put(nums[i], Math.max(ans2[j].getOrDefault(nums[i], 0), dp[i][j]));
            }
        }
        return ans;
    }
}
