class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<int>mlen(k+2);
        map<int,map<int,int>>DP;
        for(int i:nums)
        {
            map<int,map<int,int>>DP1;
            for(auto[a,b]:DP[i])
            {
                DP1[i][a]=max(DP1[i][a],b+1);
            }
            for(int j=0;j<=k;j++)DP1[i][j+1]=max(DP1[i][j+1],mlen[j]+1);
            for(auto[a,b]:DP1[i])
            {
                mlen[a]=max(mlen[a],b);
            }
            for(auto[a,b]:DP1[i])
            {
                DP[i][a]=max(DP[i][a],b);
            }
        }
        int ans=0;
        for(auto&[a,b]:DP)for(auto[c,d]:b)ans=max(ans,d);
        return ans;
    }
};
