#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(k + 1, 1));
        int ans = 1;
        vector<int> maxL(k + 1, -1);
        vector<umap<int, int>> mpp(k + 1);

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j <= k; ++j) {
                for (int l = 0; l < j; ++l) {
                    dp[i][j] = max(dp[i][j], 1 + maxL[l]);
                }

                if (mpp[j].count(nums[i])) {
                    dp[i][j] = max(dp[i][j], 1 + mpp[j][nums[i]]);
                }

                ans = max(ans, dp[i][j]);
            }

            for (int j = 0; j <= k; ++j) {
                maxL[j] = max(maxL[j], dp[i][j]);
                mpp[j][nums[i]] = max(mpp[j][nums[i]], dp[i][j]);
            }
        }

        return ans;
    }
};
