class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        vector<map<int,int>>freq(k+1);
        vector<int>rk(k+1);
        vector<set<int>>st(k+1);
        int n=nums.size();
        for(int i=0;i<n;++i)
        {
            int x=nums[i];
            int prev=0;
            for (int j=0;j<=k;++j) 
            {
                int curr=0;
                if(st[j].find(x)!=st[j].end())
                {
                    curr=1+max(prev,max(freq[j][x],rk[j]));
                }
                else
                {
                    curr=1+max(prev,freq[j][x]);
                }
                prev=rk[j];
                if (rk[j]<curr) 
                {
                    rk[j]=curr;
                    st[j].clear();
                }
                freq[j][x]=curr;
                if (curr==rk[j]) st[j].insert(x);
            }
        }
        return rk[k];
    }
};
