class Solution:
    def maximumLength(self, a: List[int], k: int) -> int:
        n=len(a)
        dp=[[1 for i in range(k+1)] for j in range(n)]
        dp1,dp2=[-1 for i in range(k + 1)],[defaultdict(int) for i in range(k+1)]
        ans=1
        for i in range(n):
            for j in range(k+1):
                for l in range(j):
                    dp[i][j]=max(dp[i][j],1+dp1[l])
                if a[i] in dp2[j]:
                    dp[i][j]=max(dp[i][j],1+dp2[j][a[i]])
                ans=max(ans,dp[i][j])
            for j in range(k+1):
                dp1[j]=max(dp1[j],dp[i][j])
                dp2[j][a[i]]=max(dp2[j][a[i]],dp[i][j])
        return ans
