#define umapci unordered_map<char,int>
#define umapll unordered_map<long long,long long>
#define f0(i,n) for(int i=0;i<n;i++)
#define f1(i,n) for(int i=1;i<=n;i++)
#define input(a) for(auto it:a) cin >> it;
