class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int len = nums.size(), maxi = 1;
        vvi a(len, vi(k+1, 1));
        vi b(k+1, -1);
        vector<mapii> mpp(k+1);
        f0(i, len){
            f0(j, k+1){
                f0(k, j) a[i][j] = max(b[k]+1,a[i][j]);
                if(mpp[j].find(nums[i]) != mpp[j].end()) a[i][j] = max(a[i][j], mpp[j][nums[i]]+1);
                maxi = max(maxi, a[i][j]);
            }
            f0(j, k+1){
                b[j] = max(b[j], a[i][j]);
                mpp[j][nums[i]] = max(mpp[j][nums[i]], a[i][j]);
            }
        }
        return maxi; 
    }
};

