class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> lengths(n, vector<int>(k + 1, 1));
        int max_length = 1;

        vector<int> max_lengths(k + 1, -1);

        vector<unordered_map<int, int>> num_to_lengths_map(k + 1);

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j <= k; ++j) {
                for (int l = 0; l < j; ++l) {
                    lengths[i][j] = max(lengths[i][j], 1 + max_lengths[l]);
                }
                if (num_to_lengths_map[j].count(nums[i])) {
                    lengths[i][j] = max(lengths[i][j], 1 + num_to_lengths_map[j][nums[i]]);
                }

                max_length = max(max_length, lengths[i][j]);
            }

            for (int j = 0; j <= k; ++j) {
                max_lengths[j] = max(max_lengths[j], lengths[i][j]);
                num_to_lengths_map[j][nums[i]] = max(num_to_lengths_map[j][nums[i]], lengths[i][j]);
            }
        }

        return max_length;
    }
};
