class Solution {
public:
    int maximumLength(vector<int>& ritik, int jee) {
        int hello = ritik.size(); 

        vector<vector<int>>nta(hello,vector<int>(jee+1,1)); 

        int ok = 1; 
        vector<int>http(jee+1,-1);

        vector<map<int,int>>neer(jee+1); 

        for(int arora=0;arora<hello;arora++)
        { 

            for(int yes=0;yes<=jee;yes++)
            {
                for(int neet=0;neet<yes;neet++)
                { 
                    nta[arora][yes]=max(nta[arora][yes], 1+http[neet]); 
                } 
                if(neer[yes].find(ritik[arora])!=neer[yes].end())
                { 
                    nta[arora][yes]=max(nta[arora][yes], 1+neer[yes][ritik[arora]]); 
                } 
                ok=max(ok,nta[arora][yes]); 
            } 
            for(int yes=0;yes<=jee;yes++)
            { 
                http[yes]=max(http[yes], nta[arora][yes]); 
                neer[yes][ritik[arora]]=max(neer[yes][ritik[arora]],nta[arora][yes]); 
            } 
        } 
        return ok; 
    }
};
