class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<int,unordered_map<int,int>> mp;

        vector<priority_queue<int>> pq;
        pq.resize(k+1);
        for(int i=0;i<=k;i++){
            pq[i].push(0);
        }

        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=k;j>=0;j--){
                //cout<<"123";
                mp[nums[i]][j]++;
                if (j-1>=0) mp[nums[i]][j]=max(mp[nums[i]][j], pq[j-1].top()+1);
                pq[j].push(mp[nums[i]][j]);
                ans=max(ans,mp[nums[i]][j]);
                //cout<<mp[nums[i]][j]<<" ";
            }
            //cout<<"\n";
        }
        return ans;
    }
};
