/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    const l = nums.length;
    const dp = Array(51).fill().map(() => Array(l));

    const map = {};

    const pos = Array(l);

    let max = Array(k+1).fill().map(() => Array(l));

    for (let i = 0; i < l; i++) {
        if (map[nums[i]] !== undefined) {
            pos[i] = map[nums[i]]; 
        } else {
            pos[i] = -1;
        }
        map[nums[i]] = i;
    }

    let res = 0;

    for (let i = 0; i < l; i++) {
        if (pos[i] === -1) {
            dp[0][i] = 1;
        } else {
            dp[0][i] = 1 + dp[0][pos[i]];
        }
        max[0][i] = Math.max(max[0][i-1] || 0, dp[0][i]);
        res = Math.max(res, dp[0][i]);
    }


    for (let i = 1; i <= k; i++) {
        for (let j = 0; j < l; j++) {
            if (pos[j] === -1) {
                dp[i][j] = (max[i-1][j-1] || 0) + 1;
            } else {
                dp[i][j] = Math.max((max[i-1][j-1] || 0) + 1, dp[i][pos[j]] + 1);
            }
            max[i][j] = Math.max(max[i][j-1] || 0, dp[i][j]);
            res = Math.max(res, dp[i][j]);
        }
    }
    return res;
};
