/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    const size = nums.length;
    const lengths = Array.from({ length: size }, () => Array(k + 1).fill(1));
    let max_length = 1;
    const max_lengths = Array(k + 1).fill(-1);
    const num_to_lengths_map = Array.from({ length: k + 1 }, () => new Map());
    for (let i = 0; i < size; ++i) {
        for (let j = 0; j <= k; ++j) {
            for (let l = 0; l < j; ++l) {
                lengths[i][j] = Math.max(lengths[i][j], 1 + max_lengths[l]);
            }
            if (num_to_lengths_map[j].has(nums[i])) {
                lengths[i][j] = Math.max(lengths[i][j], 1 + num_to_lengths_map[j].get(nums[i]));
            }

            max_length = Math.max(max_length, lengths[i][j]);
        }
        for (let j = 0; j <= k; ++j) {
            max_lengths[j] = Math.max(max_lengths[j], lengths[i][j]);
            num_to_lengths_map[j].set(nums[i], Math.max(num_to_lengths_map[j].get(nums[i]) || 0, lengths[i][j]));
        }
    }

    return max_length;
};
