class Solution {
public:
    int maximumLength( vector<int>& nums, int k) {
         int n=nums.size(); 
  //comment
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        vector<map<int,int>>skjm(k+1); 
        vector<int>skj(k+1,-1);
        int answer=1; 

        for(int i=0;i<n;i++){ 
            for(int j=0;j<=k;j++){ 
                for(int l=0;l<j;l++)
                { 
                    dp[i][j]=max(dp[i][j],1+skj[l]); 
                } 
                if(skjm[j].find(nums[i])!=skjm[j].end())
                { 
                    dp[i][j]=max(dp[i][j],1+skjm[j][nums[i]]); 
                } 
                answer=max(answer,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++)
            { 
                skj[j]=max(skj[j],dp[i][j]); 

                skjm[j][nums[i]]=max(skjm[j][nums[i]],dp[i][j]); 
            } 
        } 
        return answer; 
    }
};
