class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int arr[nums.size()][k+1];
        int i,j,ans=1;
        deque<int>st;
        int maxer[k+1];
        map<int,int>mp;
        for(i=0;i<=k;i++){
            arr[0][i]=1;
            maxer[i]=1;
        }
        mp[nums[0]]=0;
        st.push_back(0);
        for(i=1;i<nums.size();i++){
            for(j=0;j<=k;j++){
                if(j>0){
                    arr[i][j]=arr[i][j-1];
                }else{
                    arr[i][j]=1;
                }
                if(mp.find(nums[i])!=mp.end()){
                    arr[i][j]=arr[mp[nums[i]]][j]+1;
                }
                while(!st.empty()){
                    if(nums[st.back()]!=nums[i]){
                        break;
                    }
                    st.pop_back();
                }
                if(!st.empty()&&j>0){
                    arr[i][j]=max(arr[i][j],maxer[j-1]+1);
                }
                ans=max(ans,arr[i][j]);
            }
            st.push_back(i);
            mp[nums[i]]=i;
            for(j=0;j<=k;j++){
                maxer[j]=max(maxer[j],arr[i][j]);
            }
        }
        return ans;
    }
};
