class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        if k == 0:
            cnt = Counter(nums)
            return max(cnt.values())
        dp = [0 for _ in range(k+1)]
        st = [set() for _ in range(k+1)]
        cnt = [defaultdict(int) for _ in range(k+1)]

        for x in nums:
            prev = 0
            for j in range(k+1):
                cur = max(prev + 1, cnt[j][x] + 1)
                if x in st[j]:
                    cur = max(cur, (dp[j]+1))
                prev, cnt[j][x] = dp[j], cur
                if dp[j] < cur:
                    dp[j] = cur
                    st[j].clear()
                if cur == dp[j]:
                    st[j].add(x)
        return dp[k]
