class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size(); 
        vector<vector<int>>dp(n,vector<int>(k+10,1)); 
        int res=1; 
        vector<int>a(k+1,-1); 
        vector<map<int,int>>f(k+1); 
        for(int i=0;i<n;i++){

            for(int j=0;j<=k;j++){

                for(int l=0;l<j;l++){ 

                    dp[i][j]=max(dp[i][j],1+a[l]); 
                } 

                if(f[j].find(nums[i])!=f[j].end()){ 

                    dp[i][j]=max(dp[i][j],1+f[j][nums[i]]); 
                } 
                res=max(res,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++){ 

                a[j]=max(a[j],dp[i][j]); 

                f[j][nums[i]]=max(f[j][nums[i]],dp[i][j]); 
            } 
        } 
        return res; 

    }
};
