using ll = long long;
using vi = vector<int>;
using vii = vector<pair<int, int>>;
using pi = pair<int, int>;

class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size(); 
        int dp[n][k + 1]; 

        fr(i,0,n)
            clr(dp[i]); 

        int ans = 1; 

        map<pair<int,int>,int> mp1;
        map<int,int> mp2; 

        fr(i,0,n)
        {
            dp[i][0] = 1; 
            fr(j,0,k + 1)
            {
                dp[i][j] = max(dp[i][j], 1 + mp1[{nums[i],j}]); 

                if(j != 0)
                    dp[i][j] = max(dp[i][j], 1 + mp2[j - 1]); 
            }
            fr(j,0,k+1)
            {
                mp1[{nums[i],j}] = max(mp1[{nums[i],j}],dp[i][j]); 
                mp2[j] = max(mp2[j],dp[i][j]);  
                ans = max(ans,dp[i][j]); 
            }
        }

        return ans; 
    }
};
