class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int i,j,n,l,m,val,res=1;
        n=nums.size();
        vector<vector<int> > dp(n,vector<int> (k+1,0));
        for(i=0;i<=k;i++)
            dp[0][i]=1;
        map<int,int> mp;
        vector<int> tval(k+1,1),ttval(k+1,1);
        mp[nums[0]]=0;
        for(i=1;i<n;i++)
        {
            ttval=tval;
            for(j=0;j<=k;j++)
            {
                val=0;
                if(mp.find(nums[i])!=mp.end())
                    val=max(val,dp[mp[nums[i]]][j]);
                if(j>=1)
                {
                    val=max(val,ttval[j-1]);
                }
                dp[i][j]=1+val;
                if(j>=1)
                    dp[i][j]=max(dp[i][j],dp[i][j-1]);
                tval[j]=max(tval[j],dp[i][j]);
                res=max(res,dp[i][j]);
            }
            mp[nums[i]]=i;
        }
        return res;
    }
};
