#define inf 1e9

class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {        
        int n = nums.size();
        int dp[n+1][k+1];
        int ms[k+1];
        map<int,int> mx[k+1];

        for(int i = 0 ; i <= n ; i ++)
        {
            for(int j = 0 ; j <= k ; j ++)
            {
                dp[i][j] = 0;
                ms[j] = 0;
            }
        }

        for(int i = n-1 ; i >= 0 ; i --)
        {
            for(int j = k ; j >= 0 ; j --)
            {
                dp[i][j] = max(dp[i][j] , 1 + mx[j][nums[i]]);   
                if(j > 0)
                {
                    dp[i][j] = max(dp[i][j] , 1 + ms[j-1]);
                }
                mx[j][nums[i]] = max(mx[j][nums[i]] , dp[i][j]);
                ms[j]=  max(ms[j] , dp[i][j]);
            }
        }

        int ans = 0;
        for(int i = 0 ; i < n ; i ++)
        {
            ans = max(ans , dp[i][k]);
        }
        return ans;
    }
};
