/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var maximumLength = function(nums, k) {
    let n = nums.length;
    let res = 1;
        let dp = Array.from({ length: n }, () => Array(k + 1).fill(1));

        let max_len = Array(k + 1).fill(-1);
        let unique_num = Array(k + 1).fill().map(() => new Map());

        for (let i = 0; i < n; i++) {
            for (let j = 0; j <= k; j++) {
                for (let l = 0; l < j; l++) dp[i][j] = Math.max(dp[i][j], 1 + max_len[l]);

                if (unique_num[j].has(nums[i])) dp[i][j] = Math.max(dp[i][j], 1 + unique_num[j].get(nums[i]));

                res = Math.max(res, dp[i][j]);
            }

            for (let j = 0; j <= k; j++) {
                max_len[j] = Math.max(max_len[j], dp[i][j]);
                if (!unique_num[j].has(nums[i]) || unique_num[j].get(nums[i]) < dp[i][j]) unique_num[j].set(nums[i], dp[i][j]);
            }
        }

        return res;
};
