class Solution {
public:
    int maximumLength(vector<int> v, int k) {
        int n = v.size();
        vector<vector<int>>dp(k+1,vector<int>(n));
        for(int i=0;i<n;i++)dp[0][i] = 0;
        map<int,int>tmp;
        int ans = 1;
        for(int i = n-1;i>=0;i--){
            dp[0][i] = max(dp[0][i], 1 + tmp[v[i]]);
            tmp[v[i]] = max(tmp[v[i]], dp[0][i]);
        }
        for(int x=1;x<=k;x++){
            for(int i=n-2;i>=0;i--){
                dp[x-1][i] = max(dp[x-1][i],dp[x-1][i+1]);
            }
            map<int,int>mp;
            for(int i=n-1;i>=0;i--){
                if(i+1 <n)
                    dp[x][i] = max(dp[x][i],1 + dp[x-1][i+1]);
            }
            for(int i=n-1;i>=0;i--){
                dp[x][i] = max(dp[x][i],1 + mp[v[i]]);
                mp[v[i]] = max(mp[v[i]],dp[x][i]);
            }
        }
        for(int i=0;i<n;i++){
            for(int x=0;x<=k;x++){
                ans = max(ans,dp[x][i]);
            }
        }
        return ans;
    }
};
