class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        dp = [defaultdict(int) for _ in range(k + 1)]
        ma = [0] * (k + 1)
        for i in range(n):
            for j in range(k, -1, -1):
                if j == 0:
                    dp[j][nums[i]] += 1
                else:
                    dp[j][nums[i]] = max(dp[j][nums[i]] + 1, ma[j - 1] + 1)
                ma[j] = max(ma[j], dp[j][nums[i]])
        return max(ma)
