class Solution{
public:
int maximumLength(vector<int>& nums, int k) { 
        int n=nums.size(); 
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int res=1; 
        vector<int>ans(k+1,-1); //vk-->ans
        vector<map<int,int>>mp(k+1); //vkm->mp
        for(int i=0;i<n;i++){ 
            for(int j=0;j<=k;j++){ 
                for(int l=0;l<j;l++){ 
                    dp[i][j]=max(dp[i][j],1+ans[l]); 
                } 
                if(mp[j].find(nums[i])!=mp[j].end()){ 
                    dp[i][j]=max(dp[i][j],1+mp[j][nums[i]]); 
                } 
                res=max(res,dp[i][j]); 


            } 
            for(int j=0;j<=k;j++){ 
                ans[j]=max(ans[j],dp[i][j]); 
                mp[j][nums[i]]=max(mp[j][nums[i]],dp[i][j]); 
            } 
        } 
        return res; 
    }
};
