class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int i,j,n=nums.size(),l,ans=1;
        vector<vector<int>> dp(n, vector<int> (k+1, 1)), maxi(n, vector<int> (k+1, 1));
        vector<int> prev(n, -1);
        for(i=0;i<n;++i)
        {
            for(j=i-1;j>=0;--j)
            {
                if(nums[i]==nums[j])
                {
                    prev[i]=j;
                    break;
                }
            }
        }
        for(j=0;j<=k;++j)
        {
            for(i=1;i<n;++i)
            {
                l=prev[i];
                if(l>=0) dp[i][j]=max(dp[i][j], 1+dp[l][j]);
                if(j>0) dp[i][j]=max(dp[i][j], 1+maxi[i-1][j-1]);
                maxi[i][j]=max(max(maxi[i][j], dp[i][j]), maxi[i-1][j]);
                ans = max(ans, dp[i][j]);
            }
        }

        /*for(i=1;i<n;++i)
