class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        mp=[-1]*(k+1)
        mpp=[{} for _ in range(k+1)]
        dp=[[1]*(k+1) for _ in range(len(nums))]
        ans=1
        for i in range(len(nums)):
            for j in range(k+1):
                for t in range(j):
                    dp[i][j]=max(dp[i][j],mp[t]+1)
                if nums[i] in mpp[j]:
                    dp[i][j]=max(dp[i][j],mpp[j][nums[i]]+1)
                ans=max(ans,dp[i][j])
            for j in range(k+1):
                mp[j]=max(mp[j],dp[i][j])
                mpp[j][nums[i]]=max(mpp[j].get(nums[i],0),dp[i][j])
        return ans
