class Solution:
    def maximumLength(self, nums: List[int], k: int) -> int:
        n = len(nums)
        prev_index = {}
        res = [[0] * (n + 1) for i in range(k + 1)]
        largest_len = [0] * (k + 1)
        for i, val in enumerate(nums):
            for cnt in range(k + 1):
                res[cnt][i] = max(res[cnt][i], res[cnt][prev_index.get(val, -1)] + 1)
                if cnt > 0:
                    res[cnt][i] = max(res[cnt][i], largest_len[cnt - 1] + 1)

            for cnt in range(k + 1):
                largest_len[cnt] = max(largest_len[cnt], res[cnt][i])
            prev_index[val] = i
        return max(largest_len)
