class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
         int n=nums.size();
     vector<vector<int>> dp(n,vector<int>(k+1,1));
     vector<map<int,int>> mp(k+1);
     vector<int> ans(k+1,-1);
     int result=1;
     for(int i=0;i<n;i++){
        for(int z=0;z<=k;z++){

          for(int v=0;v<z;v++){
            dp[i][z]=max(ans[v]+1,dp[i][z]);

          }
          if(mp[z].find(nums[i])!=mp[z].end()){
            dp[i][z]=max(mp[z][nums[i]]+1,dp[i][z]);
          }
          result=max(result,dp[i][z]);
        }
        for(int b=0;b<=k;b++){
            ans[b]=max(ans[b],dp[i][b]);
            mp[b][nums[i]]=max(dp[i][b],mp[b][nums[i]]);
        }
     }
     return result;;

    }
};
