class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int n=a.size();
        int dp[n][k+1];
        map<int,int> last;
        map<int,int> cnt;
        for(int i=0;i<n;i++){
            for(int j=0;j<k+1;j++){
                dp[i][j]=0;
                if(i==0){
                    dp[i][j]=1;
                }
            }
        }
        last[a[0]]=1;
        cnt[a[0]]=1;
        vector<int> maxi(k+1,1);
        // maxi[0]=1;
        for(int i=1;i<n;i++){
            dp[i][0]=++cnt[a[i]];
            // for(int j=0;j<k+1;j++) {cout<<'m'<<maxi[j]<<' ';} cout<<endl;
            for(int j=0;j<k+1;j++){
                if(last[a[i]])
                {dp[i][j]=dp[last[a[i]]-1][j]+1;
                // cout<<'p';
                }
                else 
                {dp[i][j]=1;}
                if(j) dp[i][j]=max(dp[i][j],maxi[j-1]+1);

                // cout<<dp[i][j]<<' ';
            }
            for(int j=0;j<k+1;j++){
                maxi[j]=max(maxi[j],dp[i][j]);
            }
            // cout<<"maxi"<<endl;


            last[a[i]]=i+1;
        }

        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<k+1;j++){
                // cout<<dp[i][j]<<' ';
                ans=max(ans,dp[i][j]);
            }
            // cout<<endl;
        }
        return ans;
    }
};
