class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> dp(n, vector<int>(k + 1, 1));
        int maxLength = 1;
        vector<int> maxDiffLength(k + 1, -1);
        vector<map<int, int>> maxDiffLengthWithNum(k + 1);

        for (int i = 0; i < n; ++i) {
            for (int j = 0; j <= k; ++j) {
                for (int l = 0; l < j; ++l) {
                    dp[i][j] = max(dp[i][j], 1 + maxDiffLength[l]);
                }
                if (maxDiffLengthWithNum[j].find(nums[i]) != maxDiffLengthWithNum[j].end()) {
                    dp[i][j] = max(dp[i][j], 1 + maxDiffLengthWithNum[j][nums[i]]);
                }
                maxLength = max(maxLength, dp[i][j]);
            }
            for (int j = 0; j <= k; ++j) {
                maxDiffLength[j] = max(maxDiffLength[j], dp[i][j]);
                maxDiffLengthWithNum[j][nums[i]] = max(maxDiffLengthWithNum[j][nums[i]], dp[i][j]);
            }
        }

        return maxLength;
    }
};
