class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

        int siz = nums.size();

        vector<int> maxi(k + 1, -1);
        vector<map<int, int>>  mpp(k + 1);


       int mmax = 1;
          vector<vector<int>> dp(siz, vector<int>(k + 1, 1));

        for (int i = 0; i < siz; i++) 
        {

            for (int j = 0; j <= k; j++) 
            {
                for (int x = 0; x < j; x++)
                {
                    dp[i][j] = max(dp[i][j], 1 + maxi[x]);
                }

                if ( mpp[j].find(nums[i]) !=  mpp[j].end())
                {
                    dp[i][j] = max(dp[i][j], 1 +  mpp[j][nums[i]]);
                }
                mmax = max(mmax, dp[i][j]);
            }
            for (int j = 0; j <= k; j++)
            {
                maxi[j] = max(maxi[j], dp[i][j]);

                 mpp[j][nums[i]] = max( mpp[j][nums[i]], dp[i][j]);
            }
        }


        return mmax;
    }
};
