class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> t(n,vector<int>(k+1,1));


int ans = 1;
vector<int> v(k+1,-1);
vector<map<int,int>> mp(k+1);


        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                for(int l=0;l<j;l++){
                    t[i][j]= max(t[i][j],v[l]+1);
                }

                    if(mp[j].find(nums[i])!=mp[j].end()){
                        t[i][j]=max(t[i][j],mp[j][nums[i]]+1);
                    }
                    ans=max(ans,t[i][j]);
                }

            for(int j=0;j<=k;j++){
                v[j]=max(v[j],t[i][j]);
                mp[j][nums[i]]=max(mp[j][nums[i]],t[i][j]);
            }
        }
return ans;
    }
};
