class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int ans=0;
        int n=nums.size();
        vector<vector<int>> dp(n+1,vector<int> (k+1,0));
                vector<vector<int>> maxm(n+1,vector<int> (k+1,0));

        // vector<int> maxm(k+1,0);
        map<int,vector<int>> mp;
        map<int,int> pos;

        for(int i=1;i<=n;i++){
            pos[i]=mp[nums[i-1]].size()+1;
            mp[nums[i-1]].push_back(i);
        }

        for(int i=1;i<=n;i++){
            for(int j=0;j<=k;j++){
                 int ind=pos[i];
                if(ind!=1){
                   dp[i][j]=1+dp[mp[nums[i-1]][ind-2]][j]; 
                } 
                else{
                    dp[i][j]=1;
                }

                if(j>0){
                    dp[i][j]=max(dp[i][j],1+maxm[i-1][j-1]);
                }

                maxm[i][j]=max(maxm[i][j],dp[i][j]);
                maxm[i][j]=max(maxm[i][j],maxm[i-1][j]);
                if(j>0){
                     maxm[i][j]=max(maxm[i][j],maxm[i-1][j-1]);
                                maxm[i][j]=max(maxm[i][j],maxm[i][j-1]);
                }



                ans=max(ans,dp[i][j]);
            }
        }

        // for(int i=0;i<=n;i++){
        //     for(int j=0;j<=k;j++){
        //         cout<<i<<" "<<j<<" "<<dp[i][j]<<endl;
        //      }
        // }

        return ans;
    }
};
