class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> dp(n,vector<int>(k+1,-1e9));
        for(int i=0;i<n;i++) dp[i][0]=1;
        int ans=0;
        map<int,vector<int>> m;
        vector<int> mx(k+1);
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                if(!m[nums[i]].empty()) dp[i][j]=max(m[nums[i]][j]+1,dp[i][j]);
                if(j>0) dp[i][j]=max(mx[j-1]+1,dp[i][j]);
                ans=max(ans,dp[i][j]);
            }
            for(int j=0;j<=k;j++) mx[j]=max(mx[j],dp[i][j]);
            m[nums[i]]=dp[i];
        }
        return ans;
    }
};
