class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int maxi = 1;

        vector<int> v(k+1,-1);
        vector<map<int,int>> m(k+1);

        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                for(int p=0;p<j;p++){
                    dp[i][j] = max(dp[i][j],1+v[p]);
                }

                if(m[j].find(nums[i]) != m[j].end()){ 
                    dp[i][j]=max(dp[i][j],1 + m[j][nums[i]]); 
                } 

                maxi=max(maxi,dp[i][j]); 
            }
            for(int j=0;j<=k;j++){ 
                v[j] = max(v[j],dp[i][j]); 
                m[j][nums[i]] = max(m[j][nums[i]],dp[i][j]); 
            } 
        }
        return maxi;
    }
};
