class Solution {
public:
    int maximumLength(vector<int>& A, int k) {
        int n=A.size(); 
        vector<vector<int>>dp(n,vector<int>(k+1,1)); 
        int ans=1; 
        vector<int>cnt(k+1,-1); 
        vector<map<int,int>>mp(k+1); 
        for(int i=0;i<n;i++){ 
            for(int j=0;j<=k;j++){ 
                for(int l=0;l<j;l++){ 
                    dp[i][j]=max(dp[i][j],1+cnt[l]); 
                } 
                if(mp[j].find(A[i])!=mp[j].end()){ 
                    dp[i][j]=max(dp[i][j],1+mp[j][A[i]]); 
                } 
                ans=max(ans,dp[i][j]); 
            } 
            for(int j=0;j<=k;j++){ 
                cnt[j]=max(cnt[j],dp[i][j]); 
                mp[j][A[i]]=max(mp[j][A[i]],dp[i][j]); 
            } 
        } 
        return ans; 
    }
};
