class Solution
{
public:
    int maximumLength(vector<int> &nums, int k){
        int n = nums.size(), res = 1;

        vector<int> temp(k + 1, -1);
        vector<map<int, int>> m(k + 1);
        vector<vector<int>> dp(n + 1, vector<int>(k + 1, 1));

        for (int i = 0; i < n; i++){
            for (int j = 0; j <= k; j++){
                for (int x = 0; x < j; x++)
                    dp[i][j] = max(dp[i][j], 1 + temp[x]);

                if (m[j].count(nums[i]))
                    dp[i][j] = max(dp[i][j], 1 + m[j][nums[i]]);
                res = max(res, dp[i][j]);
            }

            for (int j = 0; j <= k; j++){
                m[j][nums[i]] = max(m[j][nums[i]], dp[i][j]);
                temp[j] = max(temp[j], dp[i][j]);
            }
        }
        return res;
    }
};
