class Solution {
    public int maximumLength(int[] nums, int k) {
        int n=nums.length;
        int res=1;
        int[] a=new int[k+1];
        int[][] arr=new int[n][k+1];
        a[0]=1;
        arr[0][0]=1;
        for(int i=1;i<n;i++){
            boolean r=false;
            arr[i][0]=1;

            for(int j=0;j<k;j++){
                arr[i][j+1]=a[j]+1;
                a[j]=Math.max(a[j],arr[i][j]);
                res=Math.max(arr[i][j],res);
            }
            a[k]=Math.max(a[k],arr[i][k]);
            res=Math.max(res,a[k]);
            for(int j=i-1;j>=0;j--){
                if(!r && nums[i]==nums[j]){
                    r=true;
                    for(int l=0;l<k+1;l++){
                        arr[i][l]=Math.max(arr[i][l],arr[j][l]+1);
                        res=Math.max(arr[i][l],res);
                        a[l]=Math.max(arr[i][l],a[l]);
                    }
                    break;
                }
            }


        }
        return res;
    }
}
