class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

        int n = nums.size();

        vector<vector<int>> dp(n+1,vector<int>(k+1,1));

        vector<map<int,int>> mp(k+1);
        vector<int> arr(k+1,-1);
        int ml = 1;

        for(int i = 0 ; i < n ; ++i ){
            for(int j = 0 ; j <= k ; ++j){
                for(int l = 0 ; l < j ; ++l){

                    dp[i][j] = max(dp[i][j],1+arr[l]);

                }

                if(mp[j].count(nums[i])){
                    dp[i][j] = max(dp[i][j],1+mp[j][nums[i]]);
                }
                ml = max(ml,dp[i][j]);



            }
            for(int a = 0 ; a <= k ; ++a){
                arr[a] = max(arr[a],dp[i][a]);
                mp[a][nums[i]] = max(mp[a][nums[i]],dp[i][a]);
            }
        }

        return ml;

    }
};
