class Solution {
    public int maximumLength(int[] nums, int k) {
        int n=nums.length;
        int ans=1;
        int[][] dp=new int[nums.length][k + 1];
        int[] vk = new int[k + 1];
        Map<Integer, Integer>[] vkm = new HashMap[k + 1];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                dp[i][j] = 1;
            }
        }
        for (int i = 0; i <= k; i++) {
            vk[i] = -1;
            vkm[i] = new HashMap<>();
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                for (int l = 0; l < j; l++) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vk[l]);
                }
                if (vkm[j].containsKey(nums[i])) {
                    dp[i][j] = Math.max(dp[i][j], 1 + vkm[j].get(nums[i]));
                }
                ans = Math.max(ans, dp[i][j]);
            }
            for (int j = 0; j <= k; j++) {
                vk[j] = Math.max(vk[j], dp[i][j]);
                vkm[j].put(nums[i], Math.max(vkm[j].getOrDefault(nums[i], 0), dp[i][j]));
            }
        }
        // for(int i=0;i<nums.length;i++){
        //     for(int m=0;m<=k;m++){
        //     }
        // }
        return ans;
    }
}
