class Solution {
public:
    int help(int i,int l,int k,vector<int>&v,vector<vector<vector<int>>>&dp){
        if(i>=v.size()){
            return 0;
        }

        if(dp[i][l+1][k]!=-1)return dp[i][l+1][k];

        int x =0;
        x = max(x,help(i+1,l,k,v,dp));

        if(l==-1){
            x = max(x,1+help(i+1,i,k,v,dp));
        }else{
            if(k>0){
                x = max(x,1+help(i+1,i,k-(v[i]!=v[l]),v,dp));
            }
            if(v[i]==v[l]){
                x =max(x,1+help(i+1,i,k,v,dp));
            }
        }

        return dp[i][l+1][k]=x;
    }
    int maximumLength(vector<int>& v, int k) {
        int n = v.size();
        vector<vector<int>> dp(n+1,vector<int>(n+1,1));

        int ans=1; 

        vector<int>dpk(k+1,-1); 

        vector<map<int,int>>dpk1(k+1); 

        for(int i=0;i<n;i++){ 
            for(int j=0;j<=k;j++){ 
                for(int j1=0;j1<j;j1++){ 
                    dp[i][j]=max(dp[i][j],1+dpk[j1]); 
                } 
                if(dpk1[j].find(v[i])!=dpk1[j].end()){ 
                    dp[i][j]=max(dp[i][j],1+dpk1[j][v[i]]); 
                } 

                ans=max(ans,dp[i][j]); 
            } 

            for(int j=0;j<=k;j++){ 
                dpk[j]=max(dpk[j],dp[i][j]); 

            } 
            for(int j=0;j<=k;j++){ 
                dpk1[j][v[i]] = max(dpk1[j][v[i]],dp[i][j]); 
            } 
        } 

        return ans; 

    }
};
