from collections import defaultdict

class Solution:
    def maximumLength(self, nums, k):
        size = len(nums)
        lengths = [[1] * (k + 1) for _ in range(size)]
        max_length = 1
        max_lengths = [-1] * (k + 1)
        num_to_lengths_map = [defaultdict(int) for _ in range(k + 1)]

        for i in range(size):
            for j in range(k + 1):
                for l in range(j):
                    lengths[i][j] = max(lengths[i][j], 1 + max_lengths[l])
                if nums[i] in num_to_lengths_map[j]:
                    lengths[i][j] = max(lengths[i][j], 1 + num_to_lengths_map[j][nums[i]])
                max_length = max(max_length, lengths[i][j])

            for j in range(k + 1):
                max_lengths[j] = max(max_lengths[j], lengths[i][j])
                num_to_lengths_map[j][nums[i]] = max(num_to_lengths_map[j][nums[i]], lengths[i][j])

        return max_length
