class Solution {
public:
    int maximumLength(vector<int>& a, int k) {
        int n=(int)a.size();
        vector<vector<int>> dp(n,vector<int>(k+1,1));
        map<pair<int,int>,int> mp;
        vector<multiset<int>> v(k+1);
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++){
                if(mp.find({a[i],j})!=mp.end()){
                    dp[i][j]=max(dp[i][j],mp[{a[i],j}]+1);
                    v[j].erase(v[j].find(mp[{a[i],j}]));
                }
            }
            for(int j=0;j<k;j++){
                if((int)v[j].size()>0) dp[i][j+1]=max(dp[i][j+1],(*v[j].rbegin())+1);
            }
            for(int j=0;j<=k;j++){
                mp[{a[i],j}]=dp[i][j];
                v[j].insert(dp[i][j]);
            }
        }
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<=k;j++) ans=max(ans,dp[i][j]);
        }
        return ans;
    }
};
