class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> largest(k+1, 0);

        map<int, map<int, int>> mp;

        for(int i=0; i<n; i++){


            for(int j=k; j>0; j--){
                mp[nums[i]][j] = max(mp[nums[i]][j]+1, largest[j-1]+1);
                largest[j] = max(mp[nums[i]][j], largest[j]);

            }
            mp[nums[i]][0] += 1;
            largest[0] = max(mp[nums[i]][0], largest[0]);
        }

        int ans = 0;
        for(int i=0; i<=k; i++){
            ans = max(ans, largest[i]);
        }

        return ans;
    }
};
