class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int maxii = 1;
        int n = nums.size();
        vector<int> check(k + 1, -1);
        vector<unordered_map<int, int>> mp(k + 1);
        vector<vector<int>> dp(n, vector<int>(k + 1, 1));

        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= k; j++) {
                for (int l = 0; l < j; l++) {
                    dp[i][j] = max(dp[i][j], 1 + check[l]);
                }

                if (mp[j].count(nums[i])) {
                    dp[i][j] = max(dp[i][j], 1 + mp[j][nums[i]]);
                }

                maxii = max(maxii, dp[i][j]);
            }

            for (int j = 0; j <= k; j++) {
                check[j] = max(check[j], dp[i][j]);
                mp[j][nums[i]] = max(mp[j].count(nums[i]) ? mp[j][nums[i]] : 0, dp[i][j]);
            }
        }

        return maxii;
    }
};
