class Solution {
public:
    int maximumLength(vector<int>& nums, int k) 
    {
        vector<int> map(nums.size());
        int max_col[k+1][nums.size()];
        max_col[0][0] = 1;
        for(int i = 1; i < nums.size(); i++)
        {
            map[i] = -1;
            for(int j = 0; j < i; j++)
            {
                if(nums[i] == nums[j])  
                    map[i] = j;
            }
        }

        int arr[k+1][nums.size()];
        for(int i = 0; i <= k; i++)//25
        {
            arr[i][0] = 1;
            max_col[i][0] = 1;
        }


        int max_v = 0;
        int tmp;
        for(int j = 1; j < nums.size(); j++)//25*10^6
        {
            if(map[j] == -1)
                arr[0][j] = 1;
            else
            {
                tmp = arr[0][map[j]] + 1;
                if(tmp > 1)
                    arr[0][j] = tmp;

            }
            if(max_col[0][j-1] > arr[0][j])
                max_col[0][j] = max_col[0][j-1];
            else
                max_col[0][j] = arr[0][j];


        }

        for(int i = 1; i <= k; i++)
        {
            for(int j = 1; j < nums.size(); j++)
            {
                max_v = arr[i-1][j];
                if(max_col[i-1][j-1] + 1 > max_v)
                    max_v = max_col[i-1][j-1] + 1;
                if(map[j] == -1)
                    ;
                else
                {
                    if(arr[i][map[j]] + 1 > max_v)
                        max_v = arr[i][map[j]] + 1;
                }
                arr[i][j] = max_v;
                if(max_col[i][j-1] > arr[i][j])
                    max_col[i][j] = max_col[i][j-1];
                else
                    max_col[i][j] = arr[i][j];
            }
        }

        /*
        for(int i = 0; i <= k; i++)
        {
            for(int j = 0; j < nums.size(); j++)
            {
                cout << "i: " << i << " j: " << j << " = " << arr[i][j] << "\n";
            }
        }
        */
        int ans = 0;
        for(int i = 0; i < nums.size(); i++)
        {
            if(arr[k][i] > ans)
                ans = arr[k][i];
        }

        return ans;
    }
};
