class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {

          int c=1,kk=0; 
        vector<map<int,int>>pp(k+1);
        vector<int>qq(k+1,-1); 
        vector<vector<int>>dp(nums.size(),vector<int>(k+1,1)); 
        vector<int>mm(k+1,-1); 

        for(int i=0;i<nums.size();i++){ 

            for(int j=0;j<=k;j++){ 

                for(int x=0;x<j;x++){ 
                    //m
                    dp[i][j]=max(1+qq[x],dp[i][j]); 
                } 

                if(pp[j].end()==pp[j].find(nums[i])){
                    //n
                } 
                else{
                    dp[i][j]=max(dp[i][j],1+pp[j][nums[i]]);

                }
                //m
                c=max(dp[i][j],c); 
            }
            int y=0;
            for(int w=0;w<=k;w++){
                 y=nums[i];
                mm[w]=max(dp[i][w],qq[w]);
                qq[w]=max(dp[i][w],qq[w]); 

                pp[w][y]=max(pp[w][y],dp[i][w]); 
            } 
        } 
        return c; 


    }
};
