int dp[5004][55],maxi[5004][55];
class Solution {
public:
    int maximumLength(vector<int>& nums, int k) {
        int n=nums.size(),ans=1;
        map<int,int> last;
        for(int i=0;i<=n;i++) for(int j=0;j<=k;j++) dp[i][j]=1,maxi[i][j]=1;
        last[nums[n-1]]=n-1;
        for(int i=n-2;i>=0;i--){
            for(int j=0;j<=k;j++){
                if(j>0) dp[i][j]=max(dp[i][j],maxi[i+1][j-1]+1);
                if(last[nums[i]]>i) dp[i][j]=max(dp[i][j],dp[last[nums[i]]][j]+1);
                ans=max(ans,dp[i][j]);
            }
            last[nums[i]]=i;
            for(int j=0;j<=k;j++) maxi[i][j]=max(maxi[i+1][j],dp[i][j]);
        }
        return ans;
    }
};
